import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Header from './components/Header';
import Dashboard from './components/Dashboard';
import Cart from './components/Cart';

const App = () => {
  const [cart, setCart] = useState([]);

  // Function to add an item to the cart
  const addToCart = (item, rentalPeriod) => {
    const existingItemIndex = cart.findIndex(cartItem => cartItem.id === item.id);

    if (existingItemIndex > -1) {
      // If the item exists, update its rental period and increment the count
      const updatedCart = [...cart];
      updatedCart[existingItemIndex] = {
        ...updatedCart[existingItemIndex],
        rentalPeriod: updatedCart[existingItemIndex].rentalPeriod + rentalPeriod,
        count: updatedCart[existingItemIndex].count + 1,
      };
      setCart(updatedCart);
    } else {
      // Add a new item to the cart
      setCart([...cart, { ...item, count: 1, rentalPeriod }]);
    }
  };

  // Function to update the rental period for an item in the cart
  const updateRentalPeriod = (index, newRentalPeriod) => {
    const updatedCart = [...cart];
    updatedCart[index] = {
      ...updatedCart[index],
      rentalPeriod: newRentalPeriod,
    };
    setCart(updatedCart);
  };

  // Function to remove an item from the cart
  const removeFromCart = (index) => {
    setCart(cart.filter((_, i) => i !== index));
  };

  // Function to calculate the total cost of items in the cart
  const calculateTotal = () => {
    return cart.reduce(
      (total, item) => total + item.price * item.count * item.rentalPeriod,
      0
    );
  };

  return (
    <Router>
      <Header cartCount={cart.length} />
      <Routes>
        {/* Dashboard Route */}
        <Route
          path="/"
          element={<Dashboard addToCart={addToCart} />}
        />
        {/* Cart Route */}
        <Route
          path="/cart"
          element={
            <Cart
              cartItems={cart}
              removeFromCart={removeFromCart}
              updateRentalPeriod={updateRentalPeriod}
              totalAmount={calculateTotal()}
            />
          }
        />
      </Routes>
    </Router>
  );
};

export default App;
