import React, { useState, useEffect } from 'react';
import { Container, Typography, Button, Grid, TextField, Card, CardContent, CardMedia } from '@mui/material';

const Cart = ({ cartItems, userDetails, handleCheckout }) => {
  const [rentalPeriodMap, setRentalPeriodMap] = useState({});

  useEffect(() => {
    // Initialize rental period map based on cart items
    const initialRentalPeriodMap = cartItems.reduce((acc, item) => {
      acc[item.id] = item.rentalPeriod || 1; // Default to 1 day if no rental period exists
      return acc;
    }, {});
    setRentalPeriodMap(initialRentalPeriodMap);
  }, [cartItems]);

  const handleRentalPeriodChange = (e, itemId) => {
    const newRentalPeriodMap = { ...rentalPeriodMap, [itemId]: Number(e.target.value) };
    setRentalPeriodMap(newRentalPeriodMap);
  };

  const handleRemoveItem = (itemId) => {
    // Remove item from cart
    const updatedCart = cartItems.filter((item) => item.id !== itemId);
    handleCheckout(updatedCart);
  };

  const handleProceedToCheckout = () => {
    // Proceed with checkout
    handleCheckout(cartItems);
  };

  return (
    <Container maxWidth="lg" sx={{ paddingY: 4 }}>
      <Typography variant="h4" sx={{ marginBottom: 3, fontWeight: 'bold', color: '#34495E' }}>
        Your Cart
      </Typography>

      {/* User Details Display */}
      <div>
        <Typography variant="h6" sx={{ marginBottom: 2 }}>
          User Details:
        </Typography>
        <Typography variant="body1" sx={{ marginBottom: 1 }}>
          <strong>Name:</strong> {userDetails.name}
        </Typography>
        <Typography variant="body1" sx={{ marginBottom: 1 }}>
          <strong>Location:</strong> {userDetails.location}
        </Typography>
        <Typography variant="body1" sx={{ marginBottom: 1 }}>
          <strong>Mobile Number:</strong> {userDetails.mobileNo}
        </Typography>
        <Typography variant="body1" sx={{ marginBottom: 2 }}>
          <strong>ID Proof:</strong> {userDetails.idProof}
        </Typography>
      </div>

      {/* Cart Items Display */}
      <Grid container spacing={4}>
        {cartItems.map((item) => (
          <Grid item xs={12} sm={6} md={4} key={item.id}>
            <Card sx={{ boxShadow: 3 }}>
              <CardMedia component="img" height="200" image={item.image} alt={item.name} />
              <CardContent>
                <Typography variant="h6" gutterBottom>{item.name}</Typography>
                <Typography variant="body2" color="text.secondary">{item.description}</Typography>
                <Typography variant="h6" color="primary">Rs.{item.price}/day</Typography>
                <TextField
                  label="Rental Period (days)"
                  type="number"
                  value={rentalPeriodMap[item.id] || ''}
                  onChange={(e) => handleRentalPeriodChange(e, item.id)}
                  sx={{ marginTop: 2, width: '100%' }}
                  inputProps={{ min: 1 }}
                />
                <Button
                  variant="outlined"
                  color="error"
                  sx={{ marginTop: 2 }}
                  onClick={() => handleRemoveItem(item.id)}
                >
                  Remove
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Checkout Button */}
      <Button
        variant="contained"
        sx={{ marginTop: 4, backgroundColor: '#1ABC9C', display: 'block', marginLeft: 'auto' }}
        onClick={handleProceedToCheckout}
      >
        Proceed to Checkout
      </Button>
    </Container>
  );
};

export default Cart;
