import React, { useState } from 'react';
import { Grid, Card, CardContent, CardMedia, Typography, Button, Container, TextField, Dialog, DialogActions, DialogContent, DialogTitle, CircularProgress } from '@mui/material';

const furnitureItems = [
  { id: 1, name: 'Modern Sofa', price: 50, image: 'sofa.jpg', description: 'Stylish sofa for modern living.' },
  { id: 2, name: 'Wooden Table', price: 30, image: 'Table.webp', description: 'Durable and elegant wooden table.' },
  { id: 3, name: 'Office Chair', price: 20, image: 'office chair.webp', description: 'Ergonomic office chair for comfort.' },
  { id: 4, name: 'King-Size Bed', price: 70, image: 'best-king-size-beds.webp', description: 'Spacious and cozy king-size bed.' },
  { id: 5, name: 'Bookshelf', price: 25, image: 'book self.jpg', description: 'Minimalistic bookshelf for storage.' },
];

const Dashboard = ({ addToCart }) => {
  const [rentalPeriodMap, setRentalPeriodMap] = useState({});
  const [searchQuery, setSearchQuery] = useState('');
  const [openModal, setOpenModal] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  const [userDetails, setUserDetails] = useState({
    name: '',
    location: '',
    mobileNo: '',
    idProof: '',
  });
  const [loading, setLoading] = useState(false); // Add loading state for feedback

  const handleRentalPeriodChange = (e, itemId) => {
    const newRentalPeriodMap = { ...rentalPeriodMap, [itemId]: Number(e.target.value) };
    setRentalPeriodMap(newRentalPeriodMap);
  };

  const handleAddToCart = (item) => {
    const rentalPeriod = rentalPeriodMap[item.id] || 1; // Default to 1 day if no input
    if (rentalPeriod <= 0) {
      alert('Please enter a valid rental period.');
    } else {
      setSelectedItem(item);
      setOpenModal(true); // Open modal to fill in user details
    }
  };

  const handleModalClose = () => {
    setOpenModal(false);
    setUserDetails({ name: '', location: '', mobileNo: '', idProof: '' }); // Reset form on modal close
  };

  const handleUserDetailsChange = (e) => {
    const { name, value } = e.target;
    setUserDetails({ ...userDetails, [name]: value });
  };

  const handleFormSubmit = async () => {
    if (Object.values(userDetails).some((value) => value === '')) {
      alert('Please fill all the details!');
      return;
    }

    setLoading(true); // Set loading to true while adding item to cart
    const rentalPeriod = rentalPeriodMap[selectedItem.id] || 1;
    await addToCart(selectedItem, rentalPeriod, userDetails); // Add item to cart

    setOpenModal(false); // Close modal after submitting the form
    setUserDetails({ name: '', location: '', mobileNo: '', idProof: '' }); // Reset user details
    setLoading(false); // Set loading to false after processing
    alert('Item added to cart successfully.');
  };

  // Filter items based on the search query (case-insensitive)
  const filteredItems = furnitureItems.filter((item) =>
    item.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <Container maxWidth="lg" sx={{ paddingY: 4 }}>
      <Typography variant="h4" sx={{ marginBottom: 3, fontWeight: 'bold', color: '#34495E' }}>
        Available Furniture
      </Typography>

      {/* Search Bar */}
      <TextField
        variant="outlined"
        label="Search for Furniture"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        sx={{ marginBottom: 3, width: '100%', maxWidth: 500 }}
      />

      {/* Loading and No items message */}
      {filteredItems.length === 0 ? (
        <Typography variant="h6" color="text.secondary" sx={{ marginTop: 2, textAlign: 'center', width: '100%' }}>
          No items found.
        </Typography>
      ) : (
        <Grid container spacing={4}>
          {filteredItems.map((item) => (
            <Grid item xs={12} sm={6} md={4} key={item.id}>
              <Card sx={{ boxShadow: 3 }}>
                <CardMedia component="img" height="200" image={item.image} alt={item.name} />
                <CardContent>
                  <Typography variant="h6" gutterBottom>{item.name}</Typography>
                  <Typography variant="body2" color="text.secondary">{item.description}</Typography>
                  <Typography variant="h6" color="primary">Rs.{item.price}/day</Typography>
                  <TextField
                    label="Rental Period (days)"
                    type="number"
                    value={rentalPeriodMap[item.id] || ''}
                    onChange={(e) => handleRentalPeriodChange(e, item.id)}
                    sx={{ marginTop: 2, width: '100%' }}
                    inputProps={{ min: 1 }}
                  />
                  <Button
                    variant="contained"
                    sx={{ marginTop: 2, backgroundColor: '#1ABC9C' }}
                    onClick={() => handleAddToCart(item)}
                  >
                    Add to Cart
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}

      {/* User Details Modal */}
      <Dialog open={openModal} onClose={handleModalClose}>
        <DialogTitle>Enter Your Details</DialogTitle>
        <DialogContent>
          <TextField
            label="Name"
            name="name"
            value={userDetails.name}
            onChange={handleUserDetailsChange}
            fullWidth
            sx={{ marginBottom: 2 }}
          />
          <TextField
            label="Location"
            name="location"
            value={userDetails.location}
            onChange={handleUserDetailsChange}
            fullWidth
            sx={{ marginBottom: 2 }}
          />
          <TextField
            label="Mobile Number"
            name="mobileNo"
            value={userDetails.mobileNo}
            onChange={handleUserDetailsChange}
            fullWidth
            sx={{ marginBottom: 2 }}
          />
          <TextField
            label="ID Proof"
            name="idProof"
            value={userDetails.idProof}
            onChange={handleUserDetailsChange}
            fullWidth
            sx={{ marginBottom: 2 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleModalClose} color="secondary">
            Cancel
          </Button>
          <Button onClick={handleFormSubmit} color="primary" disabled={loading}>
            {loading ? <CircularProgress size={24} color="inherit" /> : 'Submit'}
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default Dashboard;
